#include <iostream>
#include <string>
#include <cstdlib>
#include <cstring>

using namespace std;

const int MX_SZ = 1000;
const int CNT_ALPHABET = 26;

int main ()
{
	// input string
	string str_input;

	// counter for each char in alphabet
	int cnt_chars[CNT_ALPHABET];
	memset(cnt_chars, 0, sizeof(int) * CNT_ALPHABET);

	// output
	int counter = 0;

	getline(cin, str_input);
	str_input = str_input.substr(0, 1000);

	unsigned int indx = 0;
	unsigned int len = str_input.length();

	while(indx < len)
	{
		int val = str_input[indx] - 'a';
		indx++;

		if( val < 0 || val > 25)
		{
			continue;
		}
		else
		{
			if(cnt_chars[val] == 0)
			{
				counter++;
			}
			cnt_chars[val] = cnt_chars[val] + 1;
		}

	}
	cout << counter;
	return(0);
}
